function escondeTodasAbas(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("aba--view")}document.addEventListener("DOMContentLoaded",function(){for(var e=document.querySelectorAll(".tab-alvo__item"),t=0;t<e.length;t++)e[t].addEventListener("click",function(e){e=e.target.dataset.tabButton,e=document.querySelector(`[data-tab-id=${e}]`);escondeTodasAbas(),e.classList.add("aba--view")})});